function unfold_disqus() {
  const t =
    "open Disqus" === $("#switch-comment").attr("title")
      ? "close Disqus"
      : "open Disqus",
    e = $("#switch-comment>i");
  "none" === $("#disqus_thread").css("display")
    ?
    $("#disqus_thread").slideDown("normal", () => {
      $("#switch-comment").attr("title", t),
        e.hasClass("fa-toggle-off")
          ? e.removeClass("fa-toggle-off").addClass("fa-toggle-on")
          : e.removeClass("fa-toggle-on").addClass("fa-toggle-off");
    })
    : $("#disqus_thread").slideUp("normal", () => {
      $("#switch-comment").attr("title", t),
        e.hasClass("fa-toggle-off")
          ? e.removeClass("fa-toggle-off").addClass("fa-toggle-on")
          : e.removeClass("fa-toggle-on").addClass("fa-toggle-off");
    });
}
var commentElement = document.getElementsByClassName("comment_headling")[0];
commentElement.innerHTML += '&nbsp;<i class="far fa-comments"></i>&ensp;by Disqus?&nbsp;<i class="far fa-hand-point-right"></i>&nbsp;';
null != commentElement &&
  ((commentElement.innerHTML +=
    '<a id="switch-comment" href="javascript:void(0);" title="open Disqus" target="_self"> <i class="fa fas fa-toggle-off" aria-hidden="true"></i></a>'),
    (document.getElementById("disqus_thread").style.display = "none")),
  $("#switch-comment").click(function () {
    return unfold_disqus(), !1;
  });
