function unfold_valine() {
  const t =
    "open Valine" === $("#switch-comment").attr("title")
      ? "close Valine"
      : "open Valine",
    e = $("#switch-comment>i");
  "none" === $("#vcomment").css("display")
    ?
    $("#vcomment").slideDown("normal", () => {
      $("#switch-comment").attr("title", t),
        e.hasClass("fa-toggle-off")
          ? e.removeClass("fa-toggle-off").addClass("fa-toggle-on")
          : e.removeClass("fa-toggle-on").addClass("fa-toggle-off");
    })
    : $("#vcomment").slideUp("normal", () => {
      $("#switch-comment").attr("title", t),
        e.hasClass("fa-toggle-off")
          ? e.removeClass("fa-toggle-off").addClass("fa-toggle-on")
          : e.removeClass("fa-toggle-on").addClass("fa-toggle-off");
    });
}
var commentElement = document.getElementsByClassName("comment_headling")[0];
commentElement.innerHTML += '</br>&emsp;<b <i class="far fa-comments"></i>&nbsp;with&nbsp;<i class="fab fa-markdown"></i>&nbsp;Markdown? &nbsp;<i class="far fa-hand-point-right"></i>&nbsp;</b>';
null != commentElement &&
  ((commentElement.innerHTML +=
    '<a id="switch-comment" href="javascript:void(0);" title="open Valine" target="_self"> <i class="fa fas fa-toggle-off" aria-hidden="true"></i></a>'),
    (document.getElementById("vcomment").style.display = "none")),
  $("#switch-comment").click(function () {
    return unfold_valine(), !1;
  });
